import React from 'react';

class Test1 extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            jokes: {},
            loading: false
         }
    }
    componentDidMount(){
        this.setState({
            loading: true
        })
        fetch('https://api.chucknorris.io/jokes/random')
            .then(response =>response.json())
            .then(data => {
                this.setState({
                    jokes:data
                })
            })
    }
    render() { 
        const message = this.state.loading ?this.state.jokes.value: 'loading'
        return ( 
            <div>{message}</div>
         );
    }
}
 
export default Test1;
