import React from 'react'
import TodoItems from './TodoItems'
import todos from './TodoData'
// import Test from './test'
class App extends React.Component {
    constructor() {
        super()
        this.state = {
            todoData: todos
        }
        this.onChangeHandler=this.onChangeHandler.bind(this)
    }
    onChangeHandler(index){
        const todoList = [...todos]
        todoList[index].completed= !todoList[index].completed

        this.setState({
            todos: todoList
        })
    }
    render() {

        // const myTodo = this.state.todoData.map(function (todo,index) {
            // return <TodoItems name={todo.name} key={index} completed={todo.completed} handleChange={this.onChangeHandler} />
            // return <TodoItems todo={todo} handleChange={this.onChangeHandler}/>

        // })
        return (
            <div>
                {/* {myTodo} */}
                {this.state.todoData.map((todo,index)=>(
                    <>
                <p>{todo.name}</p> 
                <input type="checkbox" checked={this.todo.completed} onChange={()=>onChangeHandler(index)} id={index}/>
                <label>{todo.name}</label>
                </>
                ))
                }
                {/* <TodoItems todo={todo} handleChange={this.onChangeHandler}/> */}
            </div>
        )
    }
}

