import { SET_PRODUCTS } from "./constants"

const initialState={
    product:[],

}
export const productReducer =(state = initialState, action)=>{
    switch(action.type){
        case SET_PRODUCTS:
            return{
                ...state,
                product:action.product
            }
        default: return state
    }
}
